section .textbits 64Default relglobal dot_product_asmextern printf; Input:;   rcx - pointer to vector A;   rdx - pointer to vector B;   r8 - vector length (n);   r9 - pointer to resultdot_product_asm:    mov r15, 0    mov r14, r8    L1:        movss xmm1, DWORD [rcx+4*r15]         movss xmm2, DWORD [rdx+4*r15]         cvtps2pd xmm1, xmm1         cvtps2pd xmm2, xmm2         mulsd xmm1, xmm2         addsd xmm0, xmm1         inc r15        cmp r15, r14    jnz L1    cvtpd2ps xmm0, xmm0    movss [r9], xmm0    ret