section .dataprompt3 db "Sdot (Assembly): %lf", 10, 0section .textbits 64Default relglobal dot_product_asmextern printf; Input:;   rcx - pointer to vector A;   rdx - pointer to vector B;   r8 - vector length (n); Output:;   xmm0 - dot product resultdot_product_asm    sub rsp, 8*7   mov r15, 0   mov r14, r8    L1:                cvtsi2sd xmm1, [rcx+4*r15]        cvtsi2sd xmm2, [rdx+4*r15]        mulsd xmm1, xmm2        addsd xmm0, xmm1                inc r15        cmp r15, r14    jnz L1        lea rcx, [prompt3]    movq rdx, xmm0    call printf        add rsp, 8*7         ret